import './App.css';
import axios from 'axios'
import MarerialTable from 'material-table'
import React, { Component } from 'react';

const columnas=[
  {
      title:'Nombre',
      field:'name'
  },
  {
    title:'Edad',
    field:'age'
  },
  {
    title:'Numero de reguistro',
    field:'registrationNumber'
  }
]

class App extends Component {


  state = {
    estudiantes:[]
  }
  componentDidMount(){
    let url = 'https://studentregistrerweem2021.azurewebsites.net/api/StudentRetriveController?code=5ca2BTotnt21N57WYCF3bTjdRwUvxUs2EcU/DupnqeVBHTOpef75DA=='
        axios.post(url)
        .then( response =>{
            if(response.data){
              this.setState({
                estudiantes: response.data,
              })
            }
        }).catch(error =>{
            console.log(error)
        })
  }
  render(){
    return (
      <>
      <div className="container pt-5">
        <MarerialTable 
          columns={columnas}
          data={this.state.estudiantes}
          title="Estudiantes"
          options={{
              headerStyle: {
                  backgroundColor: '#212121',
                  color: '#FFF'
                }
            }}
            actions={[
              {
                  icon: 'edit',
                  tooltip: 'Editar usuario',
                  onClick: (event, rowData)=>this.abrirCerrarEditar(rowData.correo_usuario)
              },
              {
                  icon: 'remove',
                  tooltip: 'Cambiar estado',
                  onClick: (event, rowData)=>this.mostrarAlerta(rowData.correo_usuario,rowData.estado_usuario)
              }
         ]}
          localization={{
              pagination:{
                  labelRowsSelect: 'Registros',
                  firstAriaLabel: 'Primera',
                  firstTooltip: 'Primera',
                  previousAriaLabel: 'Anterior',
                  previousTooltip: 'Anterior',
                  nextAriaLabel: 'siguiente',
                  nextTooltip: 'siguiente',
                  lastAriaLabel: 'Ultima',
                  lastTooltip: 'ultima'
              },
              header:{
                  actions: 'Acciones'
              },
              toolbar:{
                    searchTooltip: 'Buscar',
                    searchPlaceholder: 'Buscar'
              }
              }}
        />
      </div>
      </>
    );
  }
}

export default App;
