import './App.css';
import axios from 'axios'
import MarerialTable from 'material-table'
import React, { Component } from 'react';

const columnas=[
  {
      title:'Nombre',
      field:'Name'
  },
  {
    title:'Edad',
    field:'Age'
  },
  {
    title:'Numero de reguistro',
    field:'registrationNumber'
  }
]

class App extends Component {


  state = {
    estudiantes:[]
  }
  componentDidMount(){
    let url = 'https://studentregistrerweem2021.azurewebsites.net/api/StudentRetriveController?code=5ca2BTotnt21N57WYCF3bTjdRwUvxUs2EcU/DupnqeVBHTOpef75DA=='
        axios.post(url, body)
        .then( response =>{
            if(response.data){
                if(response.data.error){
                    this.setState({
                        error: true,
                        errorMSG: response.data.error
                    })
                }else{
                    this.setState({
                        evento: response.data[0],
                        error: false,
                        idEvento: idEvento
                    })
                    if(response.data[0].estado_evento == 1){
                        this.setState({
                            nombreEstado: 'Ocultar'
                        })
                    }else if(response.data[0].estado_evento == 2){
                        this.setState({
                            nombreEstado: 'Dejar de ocultar'
                        })
                    }
                }
            }
        }).catch(error =>{
            console.log(error)
            this.setState({
                error: true,
                errorMSG: 'No se puede comunicar con el servidor'
            })
        })
  }
  render(){
    return (
      <>
      <div>
        <MarerialTable 
          columns={columnas}
          data={this.state.estudiantes}
          title="Estudiantes"
          options={{
              headerStyle: {
                  backgroundColor: '#212121',
                  color: '#FFF'
                }
            }}
          localization={{
              pagination:{
                  labelRowsSelect: 'Registros',
                  firstAriaLabel: 'Primera',
                  firstTooltip: 'Primera',
                  previousAriaLabel: 'Anterior',
                  previousTooltip: 'Anterior',
                  nextAriaLabel: 'siguiente',
                  nextTooltip: 'siguiente',
                  lastAriaLabel: 'Ultima',
                  lastTooltip: 'ultima'
              },
              header:{
                  actions: 'Acciones'
              },
              toolbar:{
                    searchTooltip: 'Buscar',
                    searchPlaceholder: 'Buscar'
              }
              }}
        />
      </div>
      </>
    );
  }
}

export default App;
