import Swal from 'sweetalert2'
import withReactContent from 'sweetalert2-react-content'
import './App.css';
import axios from 'axios'
import MarerialTable from 'material-table'
import React, { Component } from 'react';
import {Modal} from '@material-ui/core'


const MySwal = withReactContent(Swal)
const columnas=[
  {
      title:'Nombre',
      field:'name'
  },
  {
    title:'Edad',
    field:'age'
  },
  {
    title:'Numero de reguistro',
    field:'registrationNumber'
  }
]
const mobalStyle = {
  marginTop: '5%' 
}

class App extends Component {

  state = {
    estudiantes:[],
    estudiante:[
      {
        'name':'',
        'age':'',
        'registrationNumber':''
      }
    ],
    newEstudiante:[],
    modalInsertar: false,
    modalEditar:false
  }

  mostrarAlerta = (registro) =>{
    var mensaje
    var color
    MySwal.fire({
        title: 'Desea eliminar el usuario con registro: ' + registro,
        icon: 'warning',
        showCancelButton: true,
        confirmButtonColor: color,
        cancelButtonColor: '#3085d6',
        confirmButtonText: 'Si, eliminar'
      }).then((result) => {
        if (result.isConfirmed) {
          Swal.fire(

          )
        }
      })
  }

  manejadorSubmit = e =>{
    e.preventDefault()
  }

  manejadorChangeCrear = async e =>{
    await this.setState({
        form:{
            ...this.state.newEstudiante,
            [e.target.name]: e.target.value
        }
    })
  }

  abrirCerrarModalInsertar=()=>{
    this.setState({
        modalInsertar: !this.state.modalInsertar
    })
  }

  abrirCerrarModalEditar=(id)=>{
    if(this.state.modalEditar == false){
      this.state.estudiantes.map(estudiante =>{
        if(estudiante.registrationNumber == id){
          this.state.estudiante.name = estudiante.name
          this.state.estudiante.age = estudiante.age
          this.state.estudiante.registrationNumber = estudiante.registrationNumber
          this.setState({
            modalEditar: !this.state.modalEditar
          })
        }
      })
    }else{
      this.setState({
        modalEditar: !this.state.modalEditar
      })
    }
  }

  componentDidMount(){
    let url = 'https://studentregistrerweem2021.azurewebsites.net/api/StudentRetriveController?code=5ca2BTotnt21N57WYCF3bTjdRwUvxUs2EcU/DupnqeVBHTOpef75DA=='
        axios.post(url)
        .then( response =>{
            if(response.data){
              this.setState({
                estudiantes: response.data,
              })
            }
        }).catch(error =>{
            console.log(error)
        })
  }

  manejadorBotonCrear =() =>{
    console.log(this.state.newEstudiante)
  }

  render(){

    const bodyInsertar=(
      <div className="container white position-sticky" style={mobalStyle}>
        <br/>
        <h2>Agregar estudiante</h2>
          <form onSubmit={this.manejadorSubmit} method="POST" id='formUser'>
            <div className="mb-3">
              <label className="labelCorreo">Nombre</label>
              <input type="text" className="form-control" id="nombreEstudiante" name="nombreEstudiante" aria-describedby="emailHelp" required  onChange={this.manejadorChangeCrear}/>
            </div>
            <div className="mb-3">
              <label className="labelCorreo">Edad</label>
              <input type="numbre" className="form-control" id="edadEstudiante" name="edadEstudiante" aria-describedby="emailHelp" required  onChange={this.manejadorChangeCrear}/>
            </div>
            <div className="mb-3">
              <label className="labelCorreo">Nro registro</label>
              <input type="numbre" className="form-control" id="registroEstudiante" name="registroEstudiante" aria-describedby="emailHelp" required  onChange={this.manejadorChangeCrear}/>
            </div>
            <button type="submit" className="btn btn-light" id="iniciarSesion" onClick={this.manejadorBotonCrear}>Guardar</button>
            <button type="submit" className="btn btn-light" id="iniciarSesion" onClick={this.abrirCerrarModalInsertar}>Cancelar</button>
          </form>
          <br/>
      </div>
    )

    const bodyEditar=(
      <div className="container white position-sticky" style={mobalStyle}>
          <br/>
        <h2>Editar estudiante</h2>
          <form onSubmit={this.manejadorSubmit} method="POST" id='formUser'>
            <div className="mb-3">
              <label className="labelCorreo">Nombre</label>
              <input type="text" className="form-control" id="nombreEstudiante" name="nombreEstudiante" aria-describedby="emailHelp" required  onChange={this.manejadorChangeEditar} defaultValue={this.state.estudiante.name}/>
            </div>
            <div className="mb-3">
              <label className="labelCorreo">Edad</label>
              <input type="numbre" className="form-control" id="edadEstudiante" name="edadEstudiante" aria-describedby="emailHelp" required  onChange={this.manejadorChangeEditar} defaultValue={this.state.estudiante.age}/>
            </div>
            <div className="mb-3">
              <label className="labelCorreo">Nro registro</label>
              <input type="numbre" className="form-control" id="registroEstudiante" name="registroEstudiante" aria-describedby="emailHelp" required  disabled value={this.state.estudiante.registrationNumber}/>
            </div>
            <button type="submit" className="btn btn-light" id="iniciarSesion" onClick={this.manejadorBotonEditar}>Guardar</button>
            <button type="submit" className="btn btn-light" id="iniciarSesion" onClick={this.abrirCerrarModalEditar}>Cancelar</button>
          </form>
          <br/>
      </div>
    )


    return (
      <>
      <div className="container pt-5">
        <button className='btn btn-dark' onClick={this.abrirCerrarModalInsertar}>Crear usuario</button>
        <br/>
        <br/>
        <MarerialTable 
          columns={columnas}
          data={this.state.estudiantes}
          title="Estudiantes"
          options={{
            actionsColumnIndex:-1,
              headerStyle: {
                  backgroundColor: '#212121',
                  color: '#FFF'
                }
            }}
          actions={[
            {
                icon: 'edit',
                tooltip: 'Editar usuario',
                onClick: (event, rowData)=>this.abrirCerrarModalEditar(rowData.registrationNumber)
            },
            {
                icon: 'remove',
                tooltip: 'Cambiar estado',
                onClick: (event, rowData)=>this.mostrarAlerta(rowData.registrationNumber)
            }
          ]}

          localization={{
              pagination:{
                  labelRowsSelect: 'Registros',
                  firstAriaLabel: 'Primera',
                  firstTooltip: 'Primera',
                  previousAriaLabel: 'Anterior',
                  previousTooltip: 'Anterior',
                  nextAriaLabel: 'siguiente',
                  nextTooltip: 'siguiente',
                  lastAriaLabel: 'Ultima',
                  lastTooltip: 'ultima'
              },
              header:{
                  actions: 'Acciones'
              },
              toolbar:{
                    searchTooltip: 'Buscar',
                    searchPlaceholder: 'Buscar'
              }
              }}
        />
      </div>
        <Modal
          open={this.state.modalInsertar}
          onClose={this.abrirCerrarModalInsertar}>
            {bodyInsertar}
        </Modal>

        <Modal
          open={this.state.modalEditar}
          onClose={this.abrirCerrarEditar}>
              {bodyEditar}
        </Modal>
      </>
    );
  }
}

export default App;
